mapper.select:
select rac.*
	  ,(select count(*) 
		  from racBooking 
		 where car_no = rac.car_no
           and (((#{chkIn} >= concat(cbk_chkInDate,cbk_chkInTime)) and #{chkIn} < (concat(cbk_chkOutDate,cbk_chkOutTime)))
                or
                ((#{chkIn} < concat(cbk_chkInDate,cbk_chkInTime)) and (#{chkOut} > concat(cbk_chkInDate,cbk_chkInTime)))
               )
		) as booking_cnt
  from rentAcar rac
 where car_location = #{select01}
   and car_branch = #{select02}

   and car_kubun in (#{car_kubun01}, #{car_kubun02}, #{car_kubun03}, #{car_kubun04});
